import React, { useState, useContext, useEffect } from 'react';
import { 
  SafeAreaView,
  View,
  Text,
  TouchableWithoutFeedback,
  TouchableOpacity,
  FlatList,
  Modal,
  TextInput,
  Platform,
  Alert,
  StyleSheet
} from 'react-native';
import { MusicContext } from '../utilities/store';
import Entypo from 'react-native-vector-icons/Entypo';

const PlaylistsScreen = ({ route, navigation }) => {

  /*
  storeData => current state of the initial state defined within useReducer (/utilities/store.js)
  playlists => list of objects in the format [{name: playlistName, tracks: [playlistTrackObjects]}]
  modalVisible => state to keep track of whether to show the modal view or not
  inputValue => state to keep track of the current input value of TextInput component
  */
  const storeData = useContext(MusicContext);
  const [playlists, setPlayLists] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [inputValue, setInputValue] = useState('');

  // Rerender component every time playlists within the useReducer store changes (utilities/store.js)
  useEffect(() => {
    if (storeData.state.playlists.length !== 0)
      setPlayLists(storeData.state.playlists);
    else
      setPlayLists(null);
  }, [storeData.state.playlists]);

  const showAlert = (message) => {
    if (Platform.OS === 'web') {
      window.alert(message);
    } else {
      Alert.alert(message);
    }
  };

  return (
    <SafeAreaView style={styles.container}>

    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    backgroundColor: '#f1f2ff',
    alignItems: "center",
    justifyContent: "center",
  },
});

export default PlaylistsScreen;
