import React, { useState, useContext, useEffect } from 'react';
import { 
  SafeAreaView,
  View,
  Text,
  TouchableWithoutFeedback,
  TouchableOpacity,
  FlatList,
  Modal,
  TextInput,
  Platform,
  Alert,
  StyleSheet
} from 'react-native';
import { MusicContext } from '../utilities/store';
import Entypo from 'react-native-vector-icons/Entypo';

const PlaylistsScreen = ({ route, navigation }) => {

  const storeData = useContext(MusicContext);
  const [playlists, setPlayLists] = useState(null);
  const [modalVisible, setModalVisible] = useState(false);
  const [inputValue, setInputValue] = useState('');

  useEffect(() => {
    if (storeData.state.playlists.length !== 0)
      setPlayLists(storeData.state.playlists);
    else
      setPlayLists(null);
  }, [storeData.state.playlists]);

  const showAlert = (message) => {
    if (Platform.OS === 'web') {
      window.alert(message);
    } else {
      Alert.alert(message);
    }
  };

  const playlistHandler = async (item) => {
    // First check if navigation was due to "add to favorites" action
    if (route.params) {
      let trackToAdd = route.params.track;
      const trackExists = playlists.some(playlist =>
        playlist.name === item.name && playlist.tracks.some(track => track.id === trackToAdd.id)
      );
      if (trackExists)
        showAlert("This track already exists in the playlist " + item.name);
      else {
        await storeData.dispatchFunction({
          type: "ADD_TRACK_TO_PLAYLIST",
          payload: {
            data: {playlistName: item.name, track: trackToAdd}
          }
        });
        showAlert("Track successfully added to playlist " + item.name);
        navigation.navigate(route.params.screen);
      }
    }
    else {
      navigation.navigate('playlistshelper', {
        playlistName: item.name
      });
    }
  }

  const createPlaylist = async () => {
    if (inputValue === "")
      showAlert("Please enter a playlist name!");
    else {
      // Convert to lower case and removing leading/trailing white spaces for simplicity
      let cleanedValue = inputValue.toLowerCase().trim();
      // Create the new playlist
      // If playlists is empty, we simply create the new playlist and add it to our list
      if (playlists === null) {
        await storeData.dispatchFunction({
          type: "CREATE_PLAYLIST",
          payload: {
            data: {name: cleanedValue, tracks: []}
          }
        });
      }
      else {
        // First check if playlist with the same name exists already or not
        const playlistExists = playlists.some(playlist => playlist.name === inputValue);
        if (playlistExists) {
          showAlert("Playlist with the name " + inputValue + " already exists!");
        }
        // If playlist does not exist, create it
        else {
          await storeData.dispatchFunction({
            type: "CREATE_PLAYLIST",
            payload: {
              data: {name: cleanedValue, tracks: []}
            }
          });
        }
      }
      // reset state variables and close modal
      setInputValue('');
      setModalVisible(false);
    }
  };

  const deletePlaylist = async (item) => {
    await storeData.dispatchFunction({
      type: "DELETE_PLAYLIST",
      payload: {
        data: item
      }
    });
  }

  const renderModal = () => {
    return (
      <Modal visible={modalVisible} onRequestClose={() => setModalVisible(false)}>
        <View style={styles.modal}>
          <TextInput
            style={styles.modalInput}
            placeholder="Enter playlist name"
            placeholderTextColor="black"
            value={inputValue}
            onChangeText={text => setInputValue(text)}
            onSubmitEditing={() => createPlaylist()}
          />
          <View style={{flexDirection: "row", marginTop: 10}}>
            <TouchableOpacity
              activeOpacity={0.7}
              onPress={() => setModalVisible(false)}
            >
              <View style={[styles.createPlaylistButton, {width: 120, height: 60}]}>
                <Text style={styles.createPlaylistButtonText}> Cancel </Text>
              </View>
            </TouchableOpacity>
            <TouchableOpacity
              activeOpacity={0.7}
              onPress={() => createPlaylist()}
            >
              <View style={[styles.createPlaylistButton, {width: 190, height: 60}]}>
                <Text style={styles.createPlaylistButtonText}> Create playlist </Text>
              </View>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    );
  }

  const renderItem = ({ item }) => {
    return (
      <TouchableOpacity
        activeOpacity={0.7}
        onPress={() => playlistHandler(item)}
      >
        <View style={styles.playlistButton}>
          <Text style={styles.playlistButtonText}> { item.name } </Text>
          <Entypo name='cross' size={45} style={styles.cross} onPress={() => deletePlaylist(item)} />
        </View>
      </TouchableOpacity>
    );
  }

  if (playlists === null) {
    return (
      <SafeAreaView style={styles.container}>
        <Text style={styles.emptyText}> No playlists created yet! </Text>
        <TouchableOpacity
          activeOpacity={0.7}
          onPress={() => setModalVisible(true)}
        >
          <View style={styles.createPlaylistButton}>
            <Text style={styles.createPlaylistButtonText}> Create a new playlist </Text>
          </View>
        </TouchableOpacity>
        { renderModal() }
      </SafeAreaView>
    );
  }
  return (
    <SafeAreaView style={styles.container}>
      <FlatList
        data={ playlists }
        extraData={ playlists }
        renderItem = { renderItem }
        keyExtractor={(item, index) => item.name + toString(index)}
        showsVerticalScrollIndicator={false}
      />
      <TouchableOpacity
        activeOpacity={0.7}
        onPress={() => setModalVisible(true)}
      >
        <View style={styles.createPlaylistButton}>
          <Text style={styles.createPlaylistButtonText}> Create a new playlist </Text>
        </View>
      </TouchableOpacity>
      { renderModal() }
    </SafeAreaView>
  );
};

const styles = StyleSheet.create({
  container: {
    flex: 1,
    flexDirection: "column",
    backgroundColor: '#f1f2ff',
    alignItems: "center",
    justifyContent: "center",
  },
  emptyText: {
    color: "black",
    fontSize: 35,
    fontWeight: '400',
    fontFamily: 'Roboto'
  },
  createPlaylistButton: {
    width: 270,
    height: 60,
    borderRadius: 30,
    borderColor: '#3d54dc',
    backgroundColor: "#3d54dc",
    borderWidth: 3,
    alignItems: "center",
    justifyContent: "center",
    margin: 15
  },
  createPlaylistButtonText: {
    color: "white",
    fontSize: 25,
    fontWeight: '400',
    fontFamily: 'Roboto'
  },
  modal: {
    flex: 1,
    flexDirection: "column",
    backgroundColor: '#f1f2ff',
    alignItems: "center",
    justifyContent: "center",
  },
  modalInput: {
    height: 50,
    width: 350,
    borderWidth: 1,
    borderRadius: 20,
    borderColor: "black",
    paddingLeft: 20,
    marginTop: 10,
    fontSize: 25
  },
  playlistButton: {
    flexDirection: "row",
    width: 200,
    height: 60,
    borderRadius: 30,
    borderColor: '#3d54dc',
    backgroundColor: "#3d54dc",
    borderWidth: 3,
    alignItems: "center",
    justifyContent: "center",
    marginTop: 35
  },
  playlistButtonText: {
    color: "white",
    fontSize: 25,
    fontWeight: '400',
    fontFamily: 'Roboto'
  },
  cross: {
    top: 2
  }
});

export default PlaylistsScreen;
