import React, { useState, useContext, useEffect } from 'react';
import { 
  SafeAreaView,
  View,
  Text,
  Image,
  TouchableWithoutFeedback,
  FlatList,
  TextInput,
  Dimensions,
  Alert,
  Platform,
  StyleSheet
} from 'react-native';
import { MusicContext } from '../utilities/store';
import { Audio } from 'expo-av';
import AntDesign from 'react-native-vector-icons/AntDesign';
import Entypo from 'react-native-vector-icons/Entypo';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import * as Progress from 'react-native-progress';

const PlaylistsHelper = ({ route, navigation }) => {

  /*
  storeData => current state of the initial state defined within useReducer (/utilities/store.js)
  tracksState => list containing track objects
  isPlaying => music is currently playing or not
  currentTrack => reference to the current playing music object (expo av music object)
  selectedItemId => id of the currently selected or playing music track
  progress => current state of the progress bar (between 0 and 1 inclusive)
  increment => value to be added to increment the current state of the progress bar
  duration => duration of the music track in minutes and seconds
  minutes => total minutes of the music track
  seconds => total seconds of the music track
  remainingMinutes => number of minutes remaining of the music track while it is in play
  remainingSeconds => number of seconds remaining of the music track while it is in play
  */
  const storeData = useContext(MusicContext);
  const [tracksState, setTracksState] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [selectedItemId, setSelectedItemId] = useState(null);
  const [progress, setProgress] = useState(0);
  const [increment, setIncrement] = useState(0);
  const [duration, setDuration] = useState(null);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [remainingMinutes, setRemainingMinutes] = useState(0);
  const [remainingSeconds, setRemainingSeconds] = useState(0);

  // Write progress bar and timer logic here
  useEffect(() => {

  }, []);

  // Set the header title according to the name of the current playlist
  useEffect(() => {
    navigation.setOptions({ headerTitle: "Playlist: " + route.params.playlistName });
  }, []);

  // Rerender component every time playlists within the useReducer store changes (utilities/store.js)
  useEffect(() => {
    const playlist = storeData.state.playlists.find(playlist => playlist.name === route.params.playlistName);
    const tracks = playlist.tracks.length === 0 ? null : playlist.tracks;
    setTracksState(tracks);
  }, [storeData.state.playlists]);

  // Remove the current playing track if user navigates to some other screen
  useEffect(() => {
    const removeTrack = async () => {
      await currentTrack.unloadAsync();
    }
    return () => {
      if (currentTrack !== null)
        removeTrack();
    };
  }, [currentTrack]);

  // Remove the current playing track if user removes all tracks from current playlist
  useEffect(() => {
    const removeTrack = async () => {
      await currentTrack.unloadAsync();
    }
    if ((tracksState === null) && (currentTrack !== null)) {
      removeTrack();
    }
  }, [tracksState]);

  const showAlert = (message) => {
    if (Platform.OS === 'web') {
      window.alert(message);
    } else {
      Alert.alert(message);
    }
  };

  const searchLogic = (text) => {

  }

  const play = async (item) => {

  }

  const pause = async () => {

