import React, { useState, useContext, useEffect } from 'react';
import { 
  SafeAreaView,
  View,
  Text,
  Image,
  TouchableWithoutFeedback,
  FlatList,
  TextInput,
  Dimensions,
  Alert,
  Platform,
  StyleSheet
} from 'react-native';
import { MusicContext } from '../utilities/store';
import { Audio } from 'expo-av';
import AntDesign from 'react-native-vector-icons/AntDesign';
import Entypo from 'react-native-vector-icons/Entypo';
import MaterialCommunityIcons from 'react-native-vector-icons/MaterialCommunityIcons';
import * as Progress from 'react-native-progress';

const PlaylistsHelper = ({ route, navigation }) => {

  const storeData = useContext(MusicContext);
  const [tracksState, setTracksState] = useState(null);
  const [isPlaying, setIsPlaying] = useState(false);
  const [currentTrack, setCurrentTrack] = useState(null);
  const [selectedItemId, setSelectedItemId] = useState(null);
  const [progress, setProgress] = useState(0);
  const [increment, setIncrement] = useState(0);
  const [duration, setDuration] = useState(null);
  const [minutes, setMinutes] = useState(0);
  const [seconds, setSeconds] = useState(0);
  const [remainingMinutes, setRemainingMinutes] = useState(0);
  const [remainingSeconds, setRemainingSeconds] = useState(0);

  useEffect(() => {
    let interval;
    if (isPlaying) {
      interval = setInterval(() => {
        setProgress((prevState) => {
          const newProgressValue = prevState + increment;
          return newProgressValue >= 1 ? 1 : newProgressValue;
        });
        if (remainingSeconds + 1 >= 60) {
          setRemainingSeconds(0);
          setRemainingMinutes(remainingMinutes + 1);
        }
        else {
          setRemainingSeconds(remainingSeconds + 1);
        }
      }, 1000);
    }
    return () => {
      clearInterval(interval);
    };
  }, [isPlaying, remainingSeconds, remainingMinutes]);

  useEffect(() => {
    navigation.setOptions({ headerTitle: "Playlist: " + route.params.playlistName });
  }, []);

  useEffect(() => {
    const playlist = storeData.state.playlists.find(playlist => playlist.name === route.params.playlistName);
    const tracks = playlist.tracks.length === 0 ? null : playlist.tracks;
    setTracksState(tracks);
  }, [storeData.state.playlists]);

  useEffect(() => {
    const removeTrack = async () => {
      await currentTrack.unloadAsync();
    }
    return () => {
      // Remove the current playing track if user navigates to some other screen
      if (currentTrack !== null)
        removeTrack();
    };
  }, [currentTrack]);

  useEffect(() => {
    const removeTrack = async () => {
      await currentTrack.unloadAsync();
    }
    // Remove the current playing track if user removes all tracks from current playlist
    if ((tracksState === null) && (currentTrack !== null)) {
      removeTrack();
    }
  }, [tracksState]);

  const showAlert = (message) => {
    if (Platform.OS === 'web') {
      window.alert(message);
    } else {
      Alert.alert(message);
    }
  };

  const searchLogic = (text) => {
    text = text.toLowerCase();
    if (text === "") {
      setTracksState(storeData.state.playlists.find(playlist => playlist.name === route.params.playlistName).tracks);
    }
    else {
      let filteredTracks = tracksState.filter(track => (track.name.toLowerCase().includes(text)))
      setTracksState(filteredTracks);
    }
  }

  const play = async (item) => {
    const track = new Audio.Sound();
    await track.loadAsync(item.path, { onError: (error) => {
      showAlert("Error loading track!");
      console.log(error);
      return;
    }});
    setProgress(0);
    await track.playAsync({ onError: (error) => {
      showAlert("Error playing track!");
      console.log(error);
      return;
    }});
    if (isPlaying === false)
      setIsPlaying(true);
    if (selectedItemId === null)
      setSelectedItemId(item.id);
    setCurrentTrack(track)
    // Calculate track duration in minutes and seconds
    const minutes = Math.floor(item.duration / 60);
    const seconds = item.duration % 60;
    setMinutes(minutes);
    setSeconds(seconds);
    setRemainingMinutes(0);
    setRemainingSeconds(0);
    setDuration(`${minutes} minutes and ${seconds} seconds`);
    try {
      track.setOnPlaybackStatusUpdate(async (status) => {
        if (status.didJustFinish) {
            // When track finishes, unload it and reset state variables
            await track.unloadAsync();
            setIsPlaying(false);
            setSelectedItemId(null);
            setCurrentTrack(null);
            setProgress(0);
            setIncrement(0);
            setDuration(null);
        }
      });
    } catch (error) {
      showAlert("Error while playing track");
      console.log(error);
      await track.unloadAsync();
      setIsPlaying(false);
      setSelectedItemId(null);
      setCurrentTrack(null);
      setProgress(0);
      setIncrement(0);
      setDuration(null);
      return;
    }
  }

  const pause = async () => {
    try {
      const playbackStatus = await currentTrack.getStatusAsync();
      if (playbackStatus.isLoaded) {
        await currentTrack.pauseAsync();
      }
      setIsPlaying(false);
    } catch (error) {
      showAlert("Error pausing track!");
      console.log(error);
      return;
    }
  }

  const resume = async () => {
    try {
      const playbackStatus = await currentTrack.getStatusAsync();
      if (playbackStatus.isLoaded) {
        await currentTrack.playAsync();
      }
      setIsPlaying(true);
    } catch (error) {
      showAlert("Error resuming track!");
      console.log(error);
      return;
    }
  }

  const handlePlayTrack = async (item) => {
    // At the start when no track is playing and user clicks play button icon
    if (item === null && currentTrack === null) {
      // We select the first track to be played
      item = tracksState[0];
      setIncrement(Number((1 / item.duration).toFixed(17)));
      setDuration(null);
      play(item);
    } 
    // If at start user clicks any of the track
    else if (item !== null && currentTrack === null) {
      setIncrement(Number((1 / item.duration).toFixed(17)));
      setDuration(null);
      play(item);
    }
    // First condition checks if user clicks any other track while some track is playing
    // Second condition checks if user clicks play/pause icon while some track is playing
    else if ((item !== null && currentTrack !== null) || (item === null && currentTrack !== null)) {
      if (item !== null) {
        // If same item clicked again pause and resume it accordingly
        if (item.id === selectedItemId) {
          if (isPlaying)
            pause();
          else
            resume();
        }
        // If different track clicked, unload the previous playing track and play new track
        else {
          await currentTrack.unloadAsync();
          setCurrentTrack(null);
          setIncrement(Number((1 / item.duration).toFixed(17)));
          setDuration(null);
          play(item);
        }
      }
      // If play/pause icon clicked while any track is playing, pause/resume track accordingly
      else {
        if (isPlaying)
          pause();
        else
          resume();
      }
    }
    else {
      showAlert("Some error occured. Please try again later!");
    }
  }

  const handleForwardBackward = (type) => {
    const currentTracks  = storeData.state.playlists.find(playlist => playlist.name === route.params.playlistName).tracks;
    // If no track is playing and user clicks forward/backward icons
    if (currentTrack === null) {
      showAlert("No track is currently in play");
      return;
    }
    // If only one track in current playlist and user clicks forward/backward icons
    else if (currentTracks.length === 1) {
      showAlert("No more tracks in current playlist to play!");
      return;
    }
    else {
      // Get list of current playlist tracks
      // From that list find the index of the current playing track
      const index = currentTracks.findIndex(track => Number(track.id) === Number(selectedItemId));
      let updatedId;
      // If forward icon is pressed
      if (type === "forward") {
        // If index of next track is greater than length of currentTracks, loop to first index
        // Otherwise we select the id of the next track in order
        if ((index + 1) >= currentTracks.length)
          updatedId = Number(currentTracks[0].id);
        else
          updatedId = Number(currentTracks[index + 1].id);
      }
      // If backward icon is pressed
      else if (type === "backward") {
        // If index of previous track is less than 0, loop to last index
        // Otherwise we select the id of the previous track in order
        if ((index - 1) < 0)
          updatedId = Number(currentTracks[currentTracks.length - 1].id);
        else
          updatedId = Number(currentTracks[index - 1].id);
      }
      else {
        showAlert("Something went wrong. Please try later!");
        return;
      }
      updatedId = updatedId.toString();
      // We are filtering item having updatedId from storeData because
      // our tracks might be filtered as well due to search bar which can cause some error
      const item = currentTracks.find(track => track.id === updatedId);
      setSelectedItemId(item.id);
      handlePlayTrack(item);
    }
  }

  const handleFavorite = async (item) => {
    await storeData.dispatchFunction({
      type: "HANDLE_FAVORITE",
      payload: {
        data: item
      }
    });
    setTracksState((prevTracksState) =>
      prevTracksState.map((track) =>
        track.id === item.id ? { ...track, favorite: !track.favorite } : track
      )
    );
  }

  const removeFromPlaylist = async (item) => {
    await storeData.dispatchFunction({
      type: "DELETE_TRACK_FROM_PLAYLIST",
      payload: {
        data: {playlistName: route.params.playlistName, track: item}
      }
    });
    // Now stop the track if current playing track was removed from playlist
    if ((item.id === selectedItemId) && (currentTrack !== null)) {
      await currentTrack.unloadAsync();
      setIsPlaying(false);
      setSelectedItemId(null);
      setCurrentTrack(null);
      setProgress(0);
      setIncrement(0);
      setDuration(null);
    }
  }

  const renderItem = ({ item }) => {
    const isSelected = item.id === selectedItemId;
    const isFavorite = storeData.state.tracks.find(track => track.id === item.id && track.favorite === true);
    const handleSelectedItem = () => {
      if (!isSelected)
        setSelectedItemId(item.id);
      handlePlayTrack(item);
    };
    return (
      <TouchableWithoutFeedback onPress={ () => handleSelectedItem()}>
        <View style={styles.box}>
          <Image source={ item.icon } style={styles.icon} />
          <View>
            <Text style={styles.trackName}> { item.name } </Text>
            <Text style={styles.trackPerformer}> By { item.performer } </Text>
            {isSelected ? (
              <MaterialCommunityIcons
                name="waveform"
                size={49}
                style={styles.waveformIcon}
              />
            ) : null}
          </View>
          <View style={{flex: 1, flexDirection: "row", justifyContent: "flex-end", right: 70, bottom: 4}}>
            <Entypo name='cross' size={45} style={styles.cross} onPress={() => removeFromPlaylist(item)} />
            {
              isFavorite ? (
                  <AntDesign name='heart' color="red" size={49} style={styles.heart} onPress={() => handleFavorite(item)}/>
              ) : <AntDesign name='hearto' size={49} style={styles.heart} onPress={() => handleFavorite(item)}/>
            }
          </View>
        </View>
      </TouchableWithoutFeedback>
    );
  }

  const renderPlayPause = () => {
    return isPlaying ? (
      <AntDesign name='pause' size={75} style={styles.playPause} onPress={() => handlePlayTrack(null)}/>
    ) : (
      <Entypo name='controller-play' size={75} style={styles.playPause} onPress={() => handlePlayTrack(null)} />
    );
  };

  if (tracksState === null) {
    return (
      <SafeAreaView style={[styles.container, {alignItems: "center", justifyContent: "center"}]}>
        <Text style={styles.emptyText}> No tracks in current playlist! </Text>
      </SafeAreaView>
    );
  }
  return (
    <SafeAreaView style={styles.container}>
      <View style={styles.view1}>
        <TextInput
          style={styles.searchbar}
          keyboardType="default"
          placeholder="Search tracks"
          placeholderTextColor="black"
          onChangeText={(text) => searchLogic(text)}
        />
      </View>
      <View style={styles.view2}>
        <View style={styles.topBorder}></View>
        <FlatList
          data={ tracksState }
          extraData={ tracksState }
          renderItem = { renderItem }
          keyExtractor={(item) => item.id}
          showsVerticalScrollIndicator={false}
        />
      </View>
      <View style={styles.view3}>
        <Progress.Bar 
          progress={ progress }
          width={400} 
          height={19} 
          color="black"
          unfilledColor = "#f1f2ff"
          style={styles.bar}
        />
        <View style={styles.controlsWrapper}>
          <AntDesign name='banckward' size={50} style={styles.backward} onPress={() => handleForwardBackward("backward")}/>
          {renderPlayPause()}
          <AntDesign name='forward' size={50} style={styles.forward} onPress={() => handleForwardBackward("forward")}/>
          {duration !== null && (
            <Text style={styles.duration}>
              {remainingMinutes >= 0 && remainingMinutes <= 9 ? "0" + remainingMinutes : remainingMinutes}:{remainingSeconds >= 0 && remainingSeconds <= 9 ? "0" + remainingSeconds : remainingSeconds} / {minutes >= 0 && minutes <= 9 ? "0" + minutes : minutes}:{seconds >= 0 && seconds <= 9 ? "0" + seconds : seconds}
            </Text>
          )}
        </View>
      </View>
    </SafeAreaView>
  );
};

