import * as Progress from 'react-native-progress';

const TracksScreen = ({ navigation }) => {

  /*
  storeData => current state of the initial state defined within useReducer (/utilities/store.js)
  tracksState => list containing track objects
  isPlaying => music is currently playing or not
  currentTrack => reference to the current playing music object (expo av music object)
  selectedItemId => id of the currently selected or playing music track
  progress => current state of the progress bar (between 0 and 1 inclusive)
  increment => value to be added to increment the current state of the progress bar
  duration => duration of the music track in minutes and seconds
  minutes => total minutes of the music track
  seconds => total seconds of the music track
  remainingMinutes => number of minutes remaining of the music track while it is in play
  remainingSeconds => number of seconds remaining of the music track while it is in play
